import React, { useState } from "react";
import emailjs from "emailjs-com";
import appartement from "../assets/appartement.png";
import maison from "../assets/maison.png";
import demeure from "../assets/demeure.png";
import residence from "../assets/residance.png";
import pro from "../assets/pro.png";
import "../styles/QuoteWizard.css";

const besoinsOptions = [
  { value: "Un appartement", icon: appartement, label: "Un appartement" },
  { value: "Une maison", icon: maison, label: "Une maison" },
  { value: "Une grande demeure", icon: demeure, label: "Une grande demeure" },
  {
    value: "Une résidence secondaire",
    icon: residence,
    label: "Une résidence secondaire",
  },
  {
    value: "Ménage pour professionnels",
    icon: pro,
    label: "Ménage pour professionnels",
  },
];

const initialData = {
  besoin_pour: "",
  surface: "",
  pieces: 1,
  foyer: "",
  services: [],
  nom: "",
  prenom: "",
  email: "",
  adresse: "",
  telephone: "",
  message: "",
};

const servicesOptions = [
  "Ménage à domicile",
  "Repassage à domicile",
  "Vitres",
  "Grand nettoyage",
  "État des lieux",
  "Emménagement",
  "Déménagement",
  "Remise en ordre",
  "Aide et maintien à domicile",
  "Autres",
];

const stepLabels = [
  "Parlons de vous",
  "Votre logement",
  "Services choisis",
  "Vos coordonnées",
];

export default function QuoteForm() {
  const [step, setStep] = useState(0);
  const [form, setForm] = useState(initialData);
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(false);

  // Gestion des cards de la première étape
  const handleBesoinSelect = (value) => {
    setForm((f) => ({ ...f, besoin_pour: value }));
  };

  // Gestion des services (checkbox)
  const handleServiceChange = (service) => {
    setForm((f) => ({
      ...f,
      services: f.services.includes(service)
        ? f.services.filter((s) => s !== service)
        : [...f.services, service],
    }));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const next = () => setStep((s) => Math.min(s + 1, 3));
  const prev = () => setStep((s) => Math.max(s - 1, 0));

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    emailjs
      .send(
        "TON_SERVICE_ID", // <-- À remplacer par ton service ID EmailJS
        "TON_TEMPLATE_ID", // <-- À remplacer par ton template ID EmailJS
        {
          besoin_pour: form.besoin_pour,
          surface: form.surface,
          pieces: form.pieces,
          foyer: form.foyer,
          services: form.services.join(", "),
          nom: form.nom,
          prenom: form.prenom,
          email: form.email,
          adresse: form.adresse,
          telephone: form.telephone,
          message: form.message,
        },
        "TA_USER_ID" // <-- À remplacer par ton user ID EmailJS
      )
      .then(
        () => {
          setSubmitted(true);
          setLoading(false);
        },
        (error) => {
          setLoading(false);
          alert("Erreur lors de l'envoi du formulaire : " + error.text);
        }
      );
  };

  return (
    <div className="quoteform-container">
      <h1>Demandez votre devis en ligne</h1>
      <div className="steps-bar">
        {stepLabels.map((label, i) => (
          <div key={i} className={`step${i === step ? " active" : ""}`}>
            <span className="step-number">{i + 1}</span>
            <span className="step-label">{label}</span>
          </div>
        ))}
      </div>
      <form className="quoteform" onSubmit={handleSubmit} autoComplete="off">
        {/* Étape 1 : Besoin */}
        {step === 0 && (
          <div>
            <h2>Parlons de vous</h2>
            <p className="wizard-desc">
              Aidez-nous à mieux vous connaître ! Nous trouverons pour vous
              l'intervenant idéal et les services les plus adaptés à vos besoins
              !
            </p>
            <div className="besoins-cards">
              {besoinsOptions.map((opt) => (
                <button
                  type="button"
                  key={opt.value}
                  className={`besoin-card${
                    form.besoin_pour === opt.value ? " selected" : ""
                  }`}
                  onClick={() => handleBesoinSelect(opt.value)}
                >
                  <img src={opt.icon} alt={opt.label} className="besoin-icon" />
                  <span className="besoin-label">{opt.label}</span>
                </button>
              ))}
            </div>
          </div>
        )}
        {/* Étape 2 : Détails logement */}
        {step === 1 && (
          <div>
            <h2>Votre logement</h2>
            <label>Surface *</label>
            <select
              name="surface"
              value={form.surface}
              onChange={handleChange}
              required
            >
              <option value="">Choisissez...</option>
              <option>Moins de 50m²</option>
              <option>51m² à 100m²</option>
              <option>101m² à 150m²</option>
              <option>Plus de 150m²</option>
            </select>
            <label>Nombre de pièces *</label>
            <input
              type="number"
              name="pieces"
              min="1"
              max="20"
              value={form.pieces}
              onChange={handleChange}
              required
            />
            <label>Vous vivez *</label>
            <select
              name="foyer"
              value={form.foyer}
              onChange={handleChange}
              required
            >
              <option value="">Choisissez...</option>
              <option>Seul</option>
              <option>En colocation</option>
              <option>En couple</option>
              <option>En famille</option>
            </select>
          </div>
        )}
        {/* Étape 3 : Services */}
        {step === 2 && (
          <div>
            <h2>Services choisis</h2>
            <div className="services-checkboxes">
              {servicesOptions.map((service) => (
                <label key={service} className="service-checkbox">
                  <input
                    type="checkbox"
                    checked={form.services.includes(service)}
                    onChange={() => handleServiceChange(service)}
                  />
                  {service}
                </label>
              ))}
            </div>
          </div>
        )}
        {/* Étape 4 : Coordonnées */}
        {step === 3 && (
          <div>
            <h2>Vos coordonnées</h2>
            <label>Nom *</label>
            <input
              name="nom"
              value={form.nom}
              onChange={handleChange}
              required
            />
            <label>Prénom *</label>
            <input
              name="prenom"
              value={form.prenom}
              onChange={handleChange}
              required
            />
            <label>Email *</label>
            <input
              type="email"
              name="email"
              value={form.email}
              onChange={handleChange}
              required
            />
            <label>Adresse, ville ou code postal *</label>
            <input
              name="adresse"
              value={form.adresse}
              onChange={handleChange}
              required
            />
            <label>Téléphone *</label>
            <input
              name="telephone"
              value={form.telephone}
              onChange={handleChange}
              required
            />
            <label>Message (facultatif)</label>
            <textarea
              name="message"
              value={form.message}
              onChange={handleChange}
              rows={2}
            />
          </div>
        )}
        <div className="form-nav">
          {step > 0 && (
            <button type="button" onClick={prev}>
              Précédent
            </button>
          )}
          {step < 3 && (
            <button
              type="button"
              onClick={next}
              disabled={step === 0 && !form.besoin_pour}
            >
              Suivant
            </button>
          )}
          {step === 3 && (
            <button type="submit" disabled={loading}>
              {loading ? "Envoi..." : "Envoyer"}
            </button>
          )}
        </div>
        {submitted && (
          <div className="form-success">
            Merci, votre demande a bien été envoyée ! Nous vous contacterons
            rapidement.
          </div>
        )}
      </form>
    </div>
  );
}
