import React, { useState } from "react";
import { Helmet } from "react-helmet";
import { Link } from "react-router-dom";
import logo from "../assets/logo.png";
import "../styles/QuoteForm.css";

const steps = [
  {
    title: "Parlons de vous",
    subtitle: "Le spécialiste du ménage repassage à domicile",
    description:
      "Aidez-nous à mieux vous connaître ! Nous trouverons pour vous l'intervenant idéal et les services les plus adaptés à vos besoins !",
    fields: [
      {
        label: "Vous avez un besoin pour *",
        name: "besoin_pour",
        type: "radio",
        options: [
          "Un appartement",
          "Une maison",
          "Une grande demeure",
          "Une résidence secondaire",
          "Ménage pour professionnels",
        ],
        required: true,
      },
    ],
  },
  {
    title: "Vos besoins",
    subtitle: "Précisez la surface et la composition du foyer",
    description: "",
    fields: [
      {
        label: "Quelle est sa surface* ?",
        name: "surface",
        type: "select",
        options: [
          "Moins de 50m²",
          "De 51m² à 100m²",
          "De 101m² à 150m²",
          "Plus de 150m²",
        ],
        required: true,
      },
      {
        label: "Nombre de pièces*",
        name: "pieces",
        type: "number",
        min: 1,
        max: 20,
        required: true,
      },
      {
        label: "Vous vivez*",
        name: "foyer",
        type: "select",
        options: ["Seul", "En colocation", "En couple", "En famille"],
        required: true,
      },
    ],
  },
  {
    title: "Services choisis",
    subtitle: "Quels services cherchez-vous ?",
    description: "",
    fields: [
      {
        label: "Choisissez un ou plusieurs services*",
        name: "services",
        type: "checkbox",
        options: [
          "Ménage à domicile",
          "Repassage à domicile",
          "Vitres",
          "Grand nettoyage",
          "État des lieux",
          "Emménagement",
          "Déménagement",
          "Remise en ordre",
          "Aide et maintien à domicile",
          "Autres",
        ],
        required: true,
      },
    ],
  },
  {
    title: "Vos coordonnées",
    subtitle:
      "Merci d'entrer vos coordonnées afin que nous puissions vous contacter pour établir un devis personnalisé",
    description: "",
    fields: [
      { label: "Nom*", name: "nom", type: "text", required: true },
      { label: "Prénom*", name: "prenom", type: "text", required: true },
      { label: "Email*", name: "email", type: "email", required: true },
      {
        label: "Adresse, ville ou code postal*",
        name: "adresse",
        type: "text",
        required: true,
      },
      { label: "Téléphone*", name: "telephone", type: "text", required: true },
      {
        label: "Message (facultatif)",
        name: "message",
        type: "textarea",
        required: false,
      },
    ],
  },
];

const initialData = {
  besoin_pour: "",
  surface: "",
  pieces: 1,
  foyer: "",
  services: [],
  nom: "",
  prenom: "",
  email: "",
  adresse: "",
  telephone: "",
  message: "",
};

const besoinsOptions = [
  { value: "Un appartement", icon: "apartment", color: "#4fc3f7" },
  { value: "Une maison", icon: "home", color: "#81c784" },
  { value: "Une grande demeure", icon: "villa", color: "#ffb74d" },
  { value: "Une résidence secondaire", icon: "beach_access", color: "#ba68c8" },
  { value: "Ménage pour professionnels", icon: "business_center", color: "#f06292" }
];

export default function QuoteWizard() {
  const [step, setStep] = useState(0);
  const [form, setForm] = useState(initialData);
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setForm((f) => ({
        ...f,
        services: checked
          ? [...f.services, value]
          : f.services.filter((s) => s !== value),
      }));
    } else {
      setForm((f) => ({ ...f, [name]: value }));
    }
  };

  const next = () => setStep((s) => Math.min(s + 1, steps.length - 1));
  const prev = () => setStep((s) => Math.max(s - 1, 0));

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    // Envoyer les données à votre backend ici
  };

  const currentStep = steps[step];

  return (
    <div className="wizard-container">
      <Helmet>
        <title>Demandez un devis - Ménage à Nice</title>
      </Helmet>
      <div className="wizard-topbar">
        <Link to="/" className="back-btn">
          ← Retour au site
        </Link>
        <img src={logo} alt="Logo" className="wizard-logo" />
        <span className="wizard-title">Demander un devis</span>
      </div>
      <div className="wizard-step-indicator">
        <span>
          Étape {step + 1} / {steps.length}
        </span>
        <div className="wizard-steps-bar">
          {steps.map((_, i) => (
            <div
              key={i}
              className={`wizard-step-dot${i === step ? " active" : ""}`}
            >
              {i + 1}
            </div>
          ))}
        </div>
      </div>
      <form className="wizard-form" onSubmit={handleSubmit}>
        <h2>{currentStep.title}</h2>
        {currentStep.subtitle && (
          <div className="wizard-subtitle">{currentStep.subtitle}</div>
        )}
        {currentStep.description && (
          <div className="wizard-desc">{currentStep.description}</div>
        )}
        <div className="wizard-fields">
          {currentStep.fields.map((field) => {
            if (field.type === "radio") {
              return (
                <div key={field.name} className="wizard-field">
                  <label>{field.label}</label>
                  <div className="wizard-cards">
                    {besoinsOptions.map(opt => (
                      <button
                        type="button"
                        key={opt.value}
                        className={`wizard-card${form.besoin_pour === opt.value ? " selected" : ""}`}
                        onClick={() => setForm(f => ({ ...f, besoin_pour: opt.value }))}
                        style={{ borderColor: form.besoin_pour === opt.value ? opt.color : "transparent" }}
                      >
                        <span className="material-icons" style={{ color: opt.color, fontSize: 40 }}>
                          {opt.icon}
                        </span>
                        <span className="wizard-card-label">{opt.value}</span>
                      </button>
                    ))}
                  </div>
                </div>
              );
            }
            if (field.type === "select") {
              return (
                <div key={field.name} className="wizard-field">
                  <label>{field.label}</label>
                  <select
                    name={field.name}
                    value={form[field.name]}
                    onChange={handleChange}
                    required={field.required}
                  >
                    <option value="">Choisissez...</option>
                    {field.options.map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </div>
              );
            }
            if (field.type === "checkbox") {
              return (
                <div key={field.name} className="wizard-field">
                  <label>{field.label}</label>
                  <div className="wizard-checkbox-group">
                    {field.options.map((opt) => (
                      <label key={opt} className="wizard-checkbox">
                        <input
                          type="checkbox"
                          name={field.name}
                          value={opt}
                          checked={form.services.includes(opt)}
                          onChange={handleChange}
                        />
                        {opt}
                      </label>
                    ))}
                  </div>
                </div>
              );
            }
            if (field.type === "textarea") {
              return (
                <div key={field.name} className="wizard-field">
                  <label>{field.label}</label>
                  <textarea
                    name={field.name}
                    value={form[field.name]}
                    onChange={handleChange}
                    rows={3}
                  />
                </div>
              );
            }
            // text, email, number
            return (
              <div key={field.name} className="wizard-field">
                <label>{field.label}</label>
                <input
                  type={field.type}
                  name={field.name}
                  value={form[field.name]}
                  onChange={handleChange}
                  required={field.required}
                  min={field.min}
                  max={field.max}
                />
              </div>
            );
          })}
        </div>
        <div className="wizard-nav">
          {step > 0 && (
            <button type="button" onClick={prev}>
              Précédent
            </button>
          )}
          {step < steps.length - 1 && (
            <button type="button" onClick={next}>
              Suivant
            </button>
          )}
          {step === steps.length - 1 && <button type="submit">Envoyer</button>}
        </div>
        {submitted && (
          <div className="wizard-success">
            Merci, votre demande a bien été envoyée ! Nous vous contacterons
            rapidement.
          </div>
        )}
      </form>
    </div>
  );
}
